import React, { useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Information from "./Information";
import Education from "./Education";
import Grid from "@material-ui/core/Grid";
import ActivityWork from "./ActivityWork";
import Container from "@material-ui/core/Container";
import ContextWorks from "./ContextWorks";
import Summary from "../components/Summary";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { StoreContext } from "../Context/store";

const useStyles = makeStyles((theme) => ({
  root: {},
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));
const informationSchema = yup.object().shape({
  firstName: yup.string().required("This field is required."),
  lastName: yup.string().required("This field is required."),
  age: yup.string().required("This field is required."),
  gender: yup.string().required("This field is required."),
  title: yup.string().required("This field is required."),
  idCard: yup.string().required("This field is required."),
});
const educationsSchema = yup.object().shape({
  majorName: yup.string().required("This field is required."),
  education: yup.string().required("This field is required."),
});

function getSteps() {
  return [
    "ข้อมูลส่วนบุคคล",
    "ประวัติการศึกษา",
    "กิจกรรมการทำงาน",
    "บริบทการทำงาน",
    "สรุปผลประเมิน",
  ];
}

export default function HorizontalLabelPositionBelowStepper() {
  const classes = useStyles();

  const { information, educations } = useContext(StoreContext);
  const informationForm = useForm({
    validationSchema: informationSchema,
  });
  const educationsForm = useForm({
    validationSchema: educationsSchema,
  });

  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const onSubmit = (data) => {
    console.log(data);
    if (activeStep === 0) {
      information[1](data);
    } else if (activeStep === 1) {
      educations[1](data);
    }
    handleNext();
  };
  function getStepContent(stepIndex) {
    console.log(information);
    switch (stepIndex) {
      case 0:
        return <Information formProps={informationForm} data={information} />;
      case 1:
        return <Education formProps={educationsForm} data={educations} />;
      case 2:
        return <ActivityWork />;
      case 3:
        return <ContextWorks />;
      case 4:
        return <Summary />;

      default:
        return "Unknown stepIndex";
    }
  }

  return (
    <div className={classes.root}>
      <form
        onSubmit={
          activeStep === 0
            ? informationForm.handleSubmit(onSubmit)
            : educationsForm.handleSubmit(onSubmit)
        }
      >
        <Stepper activeStep={activeStep} alternativeLabel>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        <Container fixed>
          <Grid container direction="row" justify="center" alignItems="center">
            <div>
              <div>
                {activeStep === steps.length ? (
                  <div>
                    <Typography className={classes.instructions}>
                      สมบูรณ์
                    </Typography>
                  </div>
                ) : (
                  <div>
                    <Typography className={classes.instructions}>
                      {getStepContent(activeStep)}
                    </Typography>
                    <div>
                      <Button
                        disabled={activeStep === 0 || activeStep === 4}
                        onClick={handleBack}
                        className={classes.backButton}
                      >
                        ย้อนกลับ
                      </Button>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={handleNext}
                      >
                        {activeStep === steps.length - 1
                          ? "เสร็จสิ้น"
                          : "ถัดไป"}
                      </Button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </Grid>
        </Container>
      </form>
    </div>
  );
}
