import React, { Fragment } from 'react'
import Grid from '@material-ui/core/Grid'
import { makeStyles } from '@material-ui/core/styles'
import TextField from '@material-ui/core/TextField'
import FormControl from '@material-ui/core/FormControl'
import Radio from '@material-ui/core/Radio'
import RadioGroup from '@material-ui/core/RadioGroup'
import FormLabel from '@material-ui/core/FormLabel'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormHelperText from '@material-ui/core/FormHelperText'

import '../../Css/Information.css'

const useStyles = makeStyles((theme) => ({
  root: {
    '& input': {
      backgroundColor: '#F5F5F5',
      fontFamily: 'Prompt',
    },
    '& label': {
      fontFamily: 'Prompt',
    },
  },
  formControl: {
    alignContent: 'left',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
    fontFamily: 'Prompt',
    '& legend': {
      fontFamily: 'Prompt',
    },
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
    fontFamily: 'Prompt',
  },
  formControlTitle: {
    margin: theme.spacing(1),
    minWidth: 140,
    fontFamily: 'Prompt',
  },
  formGender: {
    fontFamily: 'Prompt',
    '& legend': {
      fontFamily: 'Prompt',
    },
    '& label': {
      fontFamily: 'Prompt',
    },
  },
}))
function spu_createCookie(name, value, hours) {
  if (hours) {
    var date = new Date()
    date.setTime(date.getTime() + hours * 60 * 60 * 1000)
    var expires = '; expires=' + date.toGMTString()
  } else {
    // eslint-disable-next-line
    var expires = ''
  }

  document.cookie = name + '=' + value + expires + '; '
}
function user_location() {
  var xhttp = new XMLHttpRequest()

  xhttp.onreadystatechange = function () {
    if (this.readyState === 4 && this.status === 200) {
      const localAd = JSON.parse(this.responseText)
      spu_createCookie('ip', localAd.ip, 3)
    }
  }

  xhttp.open('GET', '//api.ipify.org?format=json', true)
  xhttp.send()
}

user_location()

export default function Information({
  data,
  setInformation,
  validateTitle,
  error,
  setError,
}) {
  const classes = useStyles()

  const { firstName, lastName, age, idCard, title, gender } = data

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              {/* คำนำหน้าชื่อ */}

              {error.titleError === undefined || error.titleError === true ? (
                <TextField
                  id="title"
                  name="title"
                  label="คำนำหน้า"
                  margin="normal"
                  fullWidth
                  className={classes.root}
                  variant="filled"
                  value={title}
                  onChange={(e) =>
                    setInformation({ ...data, title: e.target.value })
                  }
                />
              ) : (
                <div>
                  <TextField
                    id="title"
                    name="title"
                    label="คำนำหน้า"
                    margin="normal"
                    fullWidth
                    className={classes.root}
                    variant="filled"
                    value={title}
                    onChange={(e) =>
                      setInformation({ ...data, title: e.target.value })
