import React, { useRef, Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import SeletGender from "../components/SelectGender";
import SelectTitle from "../components/SelectTitle";
const useStyles = makeStyles((theme) => ({
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
}));

export default function Information({ formProps: { register, errors }, data }) {
  const classes = useStyles();
  const { title, firstName, lastName, age, gender, idCard } = data[0];
  const firstRef = useRef("");
  const lastRef = useRef("");
  const idRef = useRef("");
  const ageRef = useRef("");
  const sendValue = () => {
    console.log(firstRef.current.value);
    console.log(lastRef.current.value);
    console.log(ageRef.current.value);
    console.log(idRef.current.value);
  };

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.root}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid
              container
              direction="row"
              justify="left"
              alignItems="left"
              spacing={1}
            >
              <SelectTitle
                error={!!errors.title}
                defaultValue={title}
                ref={register({ required: true })}
              />
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                id="firstName"
                name="firstName"
                label="ชื่อ"
                margin="normal"
                variant="outlined"
                required
                error={!!errors.firstName}
                defaultValue={firstName}
                inputRef={firstRef}
                onSelect={sendValue}
                fullWidth
                ref={register({ required: true })}
              />
              {errors.firstName && (
                <p className={classes.errorMessage}>
                  {errors.firstName.message}
                </p>
              )}
            </Grid>
            <Grid item md={12} xs={12}>
              <TextField
                fullWidth
                id="lastName"
                label="นามสกุล"
                name="lastName"
                margin="normal"
                variant="outlined"
                inputRef={lastRef}
                onChange={sendValue}
                required
                error={!!errors.lastName}
                defaultValue={lastName}
                ref={register({ required: true })}
              />
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <SeletGender
                  error={!!errors.gender}
                  defaultValue={gender}
                  ref={register({ required: true })}
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="age"
                  label="อายุ(ปี)"
                  name="age"
                  margin="normal"
                  variant="outlined"
                  inputRef={ageRef}
                  onSelect={sendValue}
                  required
                  error={!!errors.age}
                  defaultValue={age}
                  ref={register({ required: true })}
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                <TextField
                  fullWidth
                  id="idCard"
                  name="idCard"
                  label="เลขประจำตัวประชาชน 13 หลัก"
                  margin="normal"
                  variant="outlined"
                  inputRef={idRef}
                  onSelect={sendValue}
                  required
                  error={!!errors.idCard}
                  defaultValue={idCard}
                  ref={register({ required: true })}
                />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
